import callApi from './util';

const apiURL = "https://strangers-things.herokuapp.com/api/2111-CSU-RM-WEB-PT";

const App = () => {
const [posts, setPosts] = useState ([]);
const [token, setToken] = useState ('');
const [user, setUser] = useState ('');
