import React, { useEffect, useState } from "react";

import Form from './Form';
import './index.css';
/**
 * Query Cat API to get cats data when page loads
 * Use the search API to filter the cats by breed
 * 
 */


const BREEDS_BASE_URL = 'https://api.thecatapi.com/v1/breeds';
const App = () => {
  const [cats, setCats] = useState([]);

  useEffect(() => {
    const getCats = async() => {
      const resp = await fetch(BREEDS_BASE_URL);
      console.log('getCats resp', resp);
      const cats = await resp.json();
      console.log('cats', cats);
      setCats(cats);
    };

    getCats();
  }, [])

  return <>
    <h1>The Cat Corner</h1>
    <Form catNames={cats} setCatNames={setCats}/>
    {
      cats.map((cat, idx) => {
        return <div key={idx}>
          <h3>{cat.name}</h3>
          <h4>{cat.id}</h4>
          <img src={cat.image? cat.image.url : ''} />
        </div>
      })
    }

  </>
}



export default App;
