     console.log(results, "RESULTS")
     const posts = results.data;
     console.log(posts, "POSTS")
     setData(posts)
   }
  return (


    <div className="App">

      <h1>
        STRANGER THINGS APP !
      </h1>
      {
        posts.map(posts =>(
          <p>
            {posts}
          </p>
        ) )
      }


    </div>
  );
}


export default App;
