// const apiURL ="https://strangers-things.herokuapp.com/api/2111-CSU-RM-WEB-PT";

function Users ({setToken, setUser}) {
    const params = useParams();
    const history = useHistory();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('')

    const handleSubmit = async (event) => {
        event.preventDefault();

        const userData = await callApi ({
            url: `/users/${params.method}`,
            method: "POST",
            body: {
                 user: {
                    username,
                    password
            }
        }})

        console.log(userData)

        if (userData.data) {
        console.log('token in account', userData.data);
      // call /users/me
        const userInfo = await callApi({ 
        url: '/users/me', 
        token: userData.data.token,
      })
      console.log('userInfo', userInfo);

    //   setToken(loginResp.data.token);
    //   setGuest({username: guestData.data.guest.username});
      console.log(username, password)
    //   if (loginResp.data.token) {
    //     history.push('/');
      console.log(userData.data.token)
        // setToken(userData.data.token);
        // setUser()
        // history.push("/");
      }
    }
//   }



    // } 


    console.log('params', params)
    return( <>
        <h1>
            Please {params.method} to continue.
        </h1>
        <form onSubmit={handleSubmit}>
            <input type='text'
            placeholder='username'
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            ></input> <br/>
            <input type='password'
            placeholder='password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            ></input><br/>
            <button type='Submit'>Submit</button>
        </form>
    </>
    )
    }

export default Users;
