    </div>

   <Route exact path="/">
     <Home user={user} set/>
   </Route>
   <Route path="/posts">
     <Posts posts={posts} setPosts={setPosts}/>
   </Route>
    <Route exact path="/users/:method">
     <Users setToken={setToken} setUser={setUser}/>
   </Route>

    </>
  );

}


export default App;
