import React from 'react';
import { useParams } from 'react-router-dom';
import  Messenger from './Messenger'
import IndPost from './IndPost';
const PostDetails = ({posts, token}) => {
  // filter the vacations to find THE vacation
  const {POST_ID} = useParams();
  const [post] = posts.filter(post => post.id === Number(POST_ID));
  console.log('Posts in  POST DEET', post);
  // You can also use Array.find here to get a filtered object instead
return <>
    <h1>POST DETAILS</h1>

    <IndPost post={post}>
      <Messenger post={post} token={token}/>
      {post.content.map(content => {
        return <div>content</div>
      })}
    </IndPost>
  </>
}

export default VacationView;
